mapper.insert:
	insert into roombooking 
select b.bookno, b.bookdate, to_date(#{checkOut},'MM/DD/YYYY'), #{bfcheckbox},null, #{gradename}
from (
  select bookno, bookdate
  from book
  where username = #{username} and bookdate = to_date(#{checkin},'MM/DD/YYYY')
  order by bookno desc
  fetch first 1 row only
) b
